/**
 * 組織所属情報です。
 */
declare class Department {
    /**
     * 会社コード。
     */
    companyCd: string;

    /**
     * 組織コード。
     */
    departmentCd: string;

    /**
     * 組織名フルパス。
     */
    departmentFullName: string;

    /**
     * 組織名。
     */
    departmentName: string;

    /**
     * 組織検索名。
     */
    departmentSearchName: string;

    /**
     * 組織セットコード。
     */
    departmentSetCd: string;

    /**
     * 組織略称。
     */
    departmentShortName: string;
}
